package com.litesuits.auto;

/**
 * @author MaTianyu @http://litesuits.com
 * @date 2015-12-17 18:08
 */
public class AutoMan {

    protected static String appPackageName = "com.litesuits.join";

    /**
     * Activate and execute the code that auto-generated by lite-auto.
     */
    @SuppressWarnings("unchecked")
    public static <T> void activateThis(T autoTarget) {
        try {
            Class claxx = Class.forName(autoTarget.getClass().getName() + "$AutoLiter");
            AutoLiter<T> autoLiter = (AutoLiter<T>) claxx.newInstance();
            autoLiter.onCreate(autoTarget);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    /**
     * Cast Object To T
     */
    public static <T> T cast(Object object) {
        return (T) object;
    }

}
